import React, { useState } from 'react';
import './Header.css';

const Header = ({ onToggleSidebar }) => {
  const [showUserMenu, setShowUserMenu] = useState(false);

  const toggleUserMenu = () => {
    setShowUserMenu(!showUserMenu);
  };

  return (
    <header className="header">
      <div className="header-left">
        <button className="menu-button" onClick={onToggleSidebar} aria-label="Toggle menu">
          <span className="hamburger"></span>
          <span className="hamburger"></span>
          <span className="hamburger"></span>
        </button>
        <h1 className="header-title">Social Media Analytics</h1>
      </div>
      <div className="header-right">
        <div className="header-actions">
          <button className="notification-btn" title="Notifications">
            üîî
            <span className="notification-badge">3</span>
          </button>
          <button className="settings-btn" title="Settings">
            ‚öôÔ∏è
          </button>
        </div>
        <div className="user-profile" onClick={toggleUserMenu}>
          <div className="user-avatar">
            <span>üë§</span>
          </div>
          <div className="user-info">
            <span className="user-name">Admin User</span>
            <span className="user-role">Administrator</span>
          </div>
          <span className="dropdown-arrow">‚ñº</span>
          {showUserMenu && (
            <div className="user-menu">
              <a href="#profile" className="menu-item">üë§ My Profile</a>
              <a href="#settings" className="menu-item">‚öôÔ∏è Settings</a>
              <a href="#help" className="menu-item">‚ùì Help</a>
              <hr className="menu-divider" />
              <a href="#logout" className="menu-item logout">üö™ Logout</a>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
